import tkinter as tk
from PIL import Image, ImageTk
from urllib.request import urlopen

root = tk.Tk()

imageUrl = "data:image/jpeg;base64,"
u = urlopen(imageUrl)
raw_data = u.read()
u.close()

photo = ImageTk.PhotoImage(data=raw_data)
label = tk.Label(root, image=photo)
label.image = photo
label.pack()

root.mainloop()
